package com.jinfeng.spark.util;import com.facebook.presto.jdbc.PrestoConnection;import com.facebook.presto.jdbc.PrestoResultSet;import com.facebook.presto.jdbc.PrestoStatement;import java.sql.DriverManager;import java.sql.SQLException;/** * Project : Spark * Package : com.jinfeng.spark.util * Author : WangJinfeng * Date : 2017-12-18 11:26 * Email : wangjinfeng@yiche.com * Phone : 152-1062-7698 */public class PrestoJDBC {    public static void main(String[] args) throws SQLException, ClassNotFoundException {        Class.forName("com.facebook.presto.jdbc.PrestoDriver");        PrestoConnection connection = (PrestoConnection) DriverManager.getConnection                ("jdbc:presto://192.168.15.46:9088/hive/bdc_at_dm",                "test", "test");        PrestoStatement stmt = (PrestoStatement) connection.createStatement();        PrestoResultSet rs = (PrestoResultSet) stmt.executeQuery("show tables");        while (rs.next()) {            System.out.println(rs.getString(1));        }        rs.close();        connection.close();    }}